import React, { Component } from 'react';
import './App.css';
import Header from './Header';
import Link from './Link';
import Products from "./Products";
import Card from './Card';
import Footer from './Footer';


class App extends Component {

  constructor(){
    super();
    this.state = {
      products: Products,
      filtered: Products,
      badges: Products
    }
  }


  componentDidMount(){
    document.querySelectorAll('.fl').forEach(element => {
        if(element.id !== "Show All"){
          this.setState({
            badges:  this.state.badges.filter(elementF => {
              return elementF.gender === element.id || elementF.brand.includes(element.id);
            })
          })
          element.firstElementChild.innerText = this.state.badges.length;
        }else{
          element.firstElementChild.innerText = this.state.badges.length;
        }
    })
  }


filterLinks(value,e){
    e.preventDefault();
      if(value !== 'Show All'){
            this.setState({
            filtered: this.state.products.filter(element =>{
              return  element.gender === value || element.brand.includes(value);
            })
          })
        }else{
          this.setState({
            filtered: this.state.products
          })
        }
        const target = e.target;
        if(!target.classList.contains('orange')){
          document.querySelectorAll('.fl').forEach(element => {
            element.firstElementChild.classList.remove('orangeBg');
            element.classList.remove('orange');
          })
          target.firstElementChild.classList.add('orangeBg');
          target.classList.add('orange');
        }
  }

  render() {
    return (
      <div className="App">
       <Header />
       <div className="wrapper">
       </div>
       <div className="con">
         <div className="Filters">
           <h2>Filtered by:</h2>
           <Link id="Show All" link="SHOW All"  onClick={(e)=> this.filterLinks('Show All',e)}/>
           <h2>Gender</h2>
           <Link id="MALE" link="Male"  onClick={(e)=> this.filterLinks('MALE',e)}/>
           <Link id="FEMALE" link="Female"  onClick={(e)=> this.filterLinks('FEMALE',e)}/>
           <h2>Brand</h2>
           <Link id="GRAND" link="LE GRAND BIKES"  onClick={(e)=> this.filterLinks('GRAND',e)} />
           <Link id="KROSS" link="KROSS"  onClick={(e)=> this.filterLinks('KROSS',e)} />
           <Link id="EXPLORER" link="EXPLORER"  onClick={(e)=> this.filterLinks('EXPLORER',e)} />
           <Link id="VISITOR" link="VISITOR"  onClick={(e)=> this.filterLinks('VISITOR',e)} />
           <Link id="PONY" link="PONY"  onClick={(e)=> this.filterLinks('PONY',e)} />
           <Link id="FORCE" link="FORCE"  onClick={(e)=> this.filterLinks('FORCE',e)} />
           <Link id="E-BIKES" link="E-BIKES"  onClick={(e)=> this.filterLinks('E-BIKES',e)} />
           <Link id="IDEAL" link="IDEAL"  onClick={(e)=> this.filterLinks('IDEAL',e)} />
         </div>
         <div className="right">
           {
            this.state.filtered.map((element,index)=> {
              return <Card key={index} id={index} img={`img/${element.image}.png`} text={element.name} price={element.price} />
            })
           }
         </div>
       </div>
        <Footer />
      </div>
    );
  }
}

export default App;
