import React, { Component } from 'react';
import Products from "./Products";
import Card from './Card';

class Filters extends Component {
	
	constructor(){
	    super();
	    this.state = {
	      prod: Products,
	      filtered: Products,
	      array: []
	    }
  	}

  	componentDidMount(){
  		if(this.state.prod !== ""){
  			document.querySelectorAll('.fl').forEach( function(element, index) {
  			this.setState({
  				arr: this.state.prod.filter(el => {
  				return element.id === el.gender || el.gender.includes(element.id);
  				})
  			})
  			element.firstElementChild.innerText = this.state.arr.length;
  		});
  		}
  		
  		
  	}

	filterLinks = (e)=> {
		e.preventDefault();
			if(e.target.id === "SHOW"){
				this.setState({
				filtered: Products
				})		
			}else{
				this.setState({
				filtered: this.state.prod.filter(function(el) {
					return el.gender === e.target.id || el.brand.includes(e.target.id);
				})
			})		
		}
	}

	render() {
		return (
			<div className="Filters">
				<div className="left">
				<h2>Filtered by:</h2>
				<a onClick={this.filterLinks} className="fl" id="SHOW" href="/">{this.props.show} <span className="badge pull-right">0</span> </a>
				<h2>Gender</h2>
				<a onClick={this.filterLinks} className="fl" id="MALE" href="/">{this.props.male} <span className="badge pull-right">0</span> </a>
				<a onClick={this.filterLinks} className="fl" id="FEMALE" href="/">{this.props.female} <span className="badge pull-right">0</span> </a>
				<h2>Brand</h2>
				<a onClick={this.filterLinks} className="fl" id="GRAND" href="/">{this.props.legrand} <span className="badge pull-right">0</span> </a>
				<a onClick={this.filterLinks} className="fl" id="KROSS" href="/">{this.props.kross} <span className="badge pull-right">0</span> </a>
				<a onClick={this.filterLinks} className="fl" id="EXPLORER" href="/">{this.props.explorer} <span className="badge pull-right">0</span> </a>
				<a onClick={this.filterLinks} className="fl" id="VISITOR" href="/">{this.props.visitor} <span className="badge pull-right">0</span> </a>
				<a onClick={this.filterLinks} className="fl" id="PONY" href="/">{this.props.pony} <span className="badge pull-right">0</span> </a>
				<a onClick={this.filterLinks} className="fl" id="FORCE" href="/">{this.props.force} <span className="badge pull-right">0</span> </a>
				<a onClick={this.filterLinks} className="fl" id="E-BIKES" href="/">{this.props.ebikes} <span className="badge pull-right">0</span> </a>
				<a onClick={this.filterLinks} className="fl" id="IDEAL" href="/">{this.props.ideal} <span className="badge pull-right">0</span> </a>
				</div>
				<div className="right">
					{
						this.state.filtered.map((element , index)=>{	
							return <Card key={index} img={`img/${element.image}.png`} text={element.name} price={element.price} />
						})
					}
				</div>
			</div>
		);
	}
}
export default Filters;
