import React, { Component } from 'react';
import Products from "./Products";

class Filters extends Component {
	
	constructor(){
	    super();
	    this.state = {
	      prod: Products,
	    }
  	}

	filterLinks = (e)=> {
		e.preventDefault();
			this.setState({
				prod: this.state.prod.filter(function(el) {
					return el.gender === e.target.id || el.brand === e.target.id;
				})
			})		
	}

	render() {
		return (
			<div className="Filters">
				<div className="left">
					
				</div>
			</div>
		);
	}
}
export default Filters;
