import React, { Component } from 'react';
import './App.css';
import Header from './Header';
import Filters from './Filters';
import Products from "./Products";
import Card from './Card';
import Footer from './Footer';
import img from './img/1.png';

class App extends Component {
  constructor(){
    super();
    this.state = {
      prod: Products,
      imgs: img
    }
  }


    componentDidMount(){
      this.setState({
      prod: this.state.prod.filter((element=>{
        return element.gender === "FEMALE";
      }))
    })
  }
  render() {
    return (
      <div className="App">
       <Header />
       <div className="wrapper">
         <div className="filtersLinks">
            <Filters show="Show All" male="Male" female="Female" legrand="LE-GRAND-BIKES" kross="KROSS" explorer="EXPLORER" visitor="VISITOR" pony="PONY" force="FORCE" ebikes="E-BIKES" ideal="IDEAL"/>
         </div>
         <div className="bikess">
           {
            this.state.prod.map((element,index)=>{
                return(
                    <Card key={index} img={`./img/${element.image}.png`} text={element.name} price={element.price} />
                  )
            })
           }
         </div>
       </div>
        <Footer />
      </div>
    );
  }
}

export default App;
