import React, { Component } from 'react';
import Products from './Products';

class Filters extends Component {

	constructor(){
		super();
		this.state = {
			prod: Products
		}
	}

	handle(){
		this.setState({
			prod: this.state.prod.filter((element=>{
				return element.gender === "MALE";
			}))
		})
	}

	render() {
		return (
			<div className="Filters">
				<h2>Filtered by:</h2>
				<a href="/">{this.props.show} <span className="badge pull-right">0</span> </a>
				<h2>Gender</h2>
				<a href="/">{this.props.male} <span className="badge pull-right">0</span> </a>
				<a href="/">{this.props.female} <span className="badge pull-right">0</span> </a>
				<h2>Brand</h2>
				<a href="/">{this.props.legrand} <span className="badge pull-right">0</span> </a>
				<a href="/">{this.props.kross} <span className="badge pull-right">0</span> </a>
				<a href="/">{this.props.explorer} <span className="badge pull-right">0</span> </a>
				<a href="/">{this.props.visitor} <span className="badge pull-right">0</span> </a>
				<a href="/">{this.props.pony} <span className="badge pull-right">0</span> </a>
				<a href="/">{this.props.force} <span className="badge pull-right">0</span> </a>
				<a href="/">{this.props.ebikes} <span className="badge pull-right">0</span> </a>
				<a href="/">{this.props.ideal} <span className="badge pull-right">0</span> </a>
			</div>
		);
	}
}
export default Filters;
