import React, { Component } from 'react';
import Products from "./Products";
import Card from './Card';

class Filters extends Component {
	
	constructor(){
	    super();
	    this.state = {
	      prod: Products,
	      filtered: []
	    }
  	}

	filterLinks = (e)=> {
		e.preventDefault();
			if(e.target.id === "SHOW"){
				this.setState({
				prod: Products
				})		
			}else{
				this.setState({
				filtered: this.state.prod.filter(function(el) {
					return el.gender === e.target.id || el.brand === e.target.id;
				})
			})		
			}
	}

	render() {
		return (
			<div className="Filters">
				<div className="left">
				<h2>Filtered by:</h2>
				<a onClick={this.filterLinks} id="SHOW" href="/">{this.props.show} <span className="badge pull-right">0</span> </a>
				<h2>Gender</h2>
				<a onClick={this.filterLinks} id="MALE" href="/">{this.props.male} <span className="badge pull-right">0</span> </a>
				<a onClick={this.filterLinks} id="FEMALE" href="/">{this.props.female} <span className="badge pull-right">0</span> </a>
				<h2>Brand</h2>
				<a onClick={this.filterLinks} href="/">{this.props.legrand} <span className="badge pull-right">0</span> </a>
				<a onClick={this.filterLinks} href="/">{this.props.kross} <span className="badge pull-right">0</span> </a>
				<a onClick={this.filterLinks} href="/">{this.props.explorer} <span className="badge pull-right">0</span> </a>
				<a onClick={this.filterLinks} href="/">{this.props.visitor} <span className="badge pull-right">0</span> </a>
				<a onClick={this.filterLinks} href="/">{this.props.pony} <span className="badge pull-right">0</span> </a>
				<a onClick={this.filterLinks} href="/">{this.props.force} <span className="badge pull-right">0</span> </a>
				<a onClick={this.filterLinks} href="/">{this.props.ebikes} <span className="badge pull-right">0</span> </a>
				<a onClick={this.filterLinks} href="/">{this.props.ideal} <span className="badge pull-right">0</span> </a>
				</div>
				<div className="right">
					{
						this.state.prod.map(element => {
							return <Card img={`img/${element.image}.png`} text={element.name} price={element.price} />
						})
					}
				</div>
			</div>
		);
	}
}
export default Filters;
