import React, { Component } from 'react';
import './App.css';
import Header from './Header';
import Link from './Link';
import Products from "./Products";
import Card from './Card';
import Footer from './Footer';


class App extends Component {

  constructor(){
    super();
    this.state = {
      products: Products,
      filtered: Products
    }
  }

  filterLinks(e,value){
    e.preventDefault();
      if(value !== 'Show All'){
            this.setState({
            filtered: this.state.products.filter(element =>{
              return  element.gender === value || element.brand.includes(value);
            })
          })
        }else{
          this.setState({
            filtered: this.state.products
          })
        }
  }


  render() {
    return (
      <div className="App">
       <Header />
       <div className="wrapper">
       </div>
       <div className="con">
         <div className="Filters">
           <h2>Filtered by:</h2>
           <Link link="SHOW All" badge={0} onClick={(e)=> this.filterLinks('Show All')}/>
           <h2>Gender</h2>
           <Link link="Male" badge={0} onClick={(e)=> this.filterLinks('MALE')}/>
           <Link link="Female" badge={0}/>
           <h2>Brand</h2>
           <Link link="Female" badge={0}/>
           <Link link="Female" badge={0}/>
           <Link link="Female" badge={0}/>
           <Link link="Female" badge={0}/>
           <Link link="Female" badge={0}/>
           <Link link="Female" badge={0}/>
           <Link link="Female" badge={0}/>
           <Link link="Female" badge={0}/>
         </div>
         <div className="right">
           {
            this.state.filtered.map((element,index)=> {
              return <Card key={index} id={index} img={`img/${element.image}.png`} text={element.name} price={element.price} />
            })
           }
         </div>
       </div>
        <Footer />
      </div>
    );
  }
}

export default App;
